import React from "react";
import PropTypes from "prop-types";

export default class ClassComponents extends React.Component {
    state = { flag: true }
    static propTypes = {
        name: PropTypes.string
    }

    render() {
        const { flag } = this.state
        return (<div>
                 <span onClick={this.changeFlag}>This is ClassComponents state test {flag ? 'true' : 'false'} {this.props.name}</span>
                 <hr/>
                 <input type='text' ref={c=>this.input1=c}></input>
                 <div onClick={this.showInput}>click me</div>
               </div>)
    }

    changeFlag = () => {
        console.log(this)
        const { flag } = this.state
        this.setState({ flag: !flag })
    }

    showInput=(event)=>{
        console.log(event.target.innerHTML)
        event.target.value =this.input1.value
    }
}
